double lngauss1(double x,double mean,double sigma,double etta){
    if (etta==0)
    	return TMath::Gaus(x,mean,sigma,1);
    double const _2ln2=1.386294361; //2*log(2)
    double const sqrt_2ln2=1.177410023;
    double const sqrt_2pi=2.506628275;
    double const cdelta=0.0000000001;
    double sigma0=log(etta*sqrt_2ln2+sqrt(1+etta*etta*_2ln2))/sqrt_2ln2;
    if (etta>0){
    	if (x<(mean+sigma/etta))
    		return 1./sqrt_2pi/sigma*etta/sigma0*exp(-1./2.*(pow(log(1.-(x-mean)*etta/sigma),2.)/sigma0/sigma0+ sigma0*sigma0));
    	else
    		return 0;//-12
    }
    else{
    	if (x>(mean+sigma/etta))
    		return 1./sqrt_2pi/sigma*etta/sigma0*exp(-1./2.*(pow(log(1.-(x-mean)*etta/sigma),2.)/sigma0/sigma0+ sigma0*sigma0));
    	else
    		return 0;//-12
    }
}

